select date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d') date_time,uld.teacher_id,uld.user_id ,uld.task_id,uld.report,(case when uld.study_type=1 then qp.exam_name else op.task_name end) task_name ,uld.operate_id,(case when uld.study_type=1 then qp.question_name else op.operate_name end) operate_name ,uld.study_type,MAX(uld.study_duration) study_duration,MAX(uld.current_score) current_score ,ROUND(MAX(uld.current_score)*ifnull(qp.prop,1),2) prop_scroe,(case when uld.study_type=1 then 100 else op.o_count*100 end) total_score -- ,MAX(uld.current_score) current_score,ifnull(qp.prop,1) prop,qp.q_count,qp.prop qp_prop,op.o_count from ( select max(id) id from user_live_data where id>0
and teacher_id=#{teacherId,jdbcType=INTEGER}
and study_type=#{studyType,jdbcType=INTEGER} and task_id=#{exameId,jdbcType=INTEGER} and date_format(FROM_UNIXTIME(start_time/1000),'%Y.%m.%d')>=date_format(#{startTime,jdbcType=VARCHAR},'%Y.%m.%d') and date_format(#{stopTime,jdbcType=VARCHAR},'%Y.%m.%d')>=date_format(FROM_UNIXTIME(start_time/1000),'%Y.%m.%d') group by teacher_id,user_id,task_id,operate_id,study_type,train_id,date_format(FROM_UNIXTIME(start_time/1000),'%Y.%m.%d') ) studybv left join user_live_data uld on uld.id=studybv.id left join ( select e.id exam_id,e.exam_name,q.id question_id,q.question_name,qprop.q_count,qprop.sum_prop,q.proportion/qprop.sum_prop prop from exam e left join questions q on e.id=q.exame_id left join ( select e.id exam_id,e.exam_name,count(q.id) q_count,SUM(q.proportion) sum_prop from exam e left join questions q on e.id=q.exame_id where e.is_deleted=0 and q.is_deleted=0 group by e.id,e.exam_name ) qprop on e.id=qprop.exam_id ) qp on qp.exam_id=uld.task_id and qp.question_id=uld.operate_id and uld.study_type=1 left join ( select t.id task_id,t.task_name,o.id operate_id,o.operate_name,oprop.o_count from task t left join task_oper_relation tor on tor.task_id=t.id left join operate o on tor.operate_id=o.id left join ( select tor.task_id,count(tor.operate_id) o_count from task_oper_relation tor left join operate o on tor.operate_id=o.id where o.is_deleted=0 group by tor.task_id ) oprop on t.id=oprop.task_id ) op on op.task_id=uld.task_id and op.operate_id=uld.operate_id and uld.study_type=0 group by uld.teacher_id,uld.user_id,uld.task_id,uld.report,uld.operate_id,uld.study_type ,date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d'),qp.q_count,qp.prop,op.o_count
) bv group by bv.user_id,bv.total_score ) gbv

select uld.teacher_id,uld.task_id xAxis,uld.study_type yAxis,count(distinct uld.user_id) userCount,date_format(FROM_UNIXTIME(uld.begin_time/1000),'%Y.%m.%d') study_date from user_score_record uld where date_format(FROM_UNIXTIME(uld.begin_time/1000),'%Y.%m.%d')>=date_format(#{startTime,jdbcType=VARCHAR},'%Y.%m.%d') and date_format(#{startTime,jdbcType=VARCHAR},'%Y.%m.%d')>=date_format(FROM_UNIXTIME(uld.begin_time/1000),'%Y.%m.%d')
and uld.teacher_id=#{teacherId,jdbcType=INTEGER}
GROUP BY uld.teacher_id,uld.task_id,uld.study_type,date_format(FROM_UNIXTIME(uld.begin_time/1000),'%Y.%m.%d') ORDER BY userCount DESC,uld.study_type,uld.teacher_id,uld.task_id limit 0,1

select uld.teacher_id,uld.task_id xAxis,uld.study_type yAxis,count(distinct uld.user_id) userCount,date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d') study_date from user_live_data uld where date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d')>=date_format(#{startTime,jdbcType=VARCHAR},'%Y.%m.%d') and date_format(#{startTime,jdbcType=VARCHAR},'%Y.%m.%d')>=date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d')
and uld.teacher_id=#{teacherId,jdbcType=INTEGER}
GROUP BY uld.teacher_id,uld.task_id,uld.study_type,date_format(FROM_UNIXTIME(uld.start_time/1000),'%Y.%m.%d') ORDER BY userCount DESC,uld.study_type,uld.teacher_id,uld.task_id limit 0,1
